import React , {useEffect} from 'react'
import {Link, useNavigate} from 'react-router-dom'
import decode from 'jwt-decode'
import { useSelector , useDispatch } from 'react-redux'
import logo from '../../assets/logo.png'
import search from '../../assets/search.svg'
import Avatar from '../../components/Avatar/Avatar'
import './navbar.css'
import { setCurrentuser } from '../../actions/currentUser'

const navbar = () => {
    const dispatch= useDispatch()
    var User= useSelector((state)=> (state.currentUserReducer))
    const navigate= useNavigate()

    const handleLogout= () =>{
      dispatch({type: 'LOGOUT'});
      navigate('/')
      dispatch(setCurrentuser(null))
    }
     
    useEffect(() => {
      const token= User?.token
      if(token){
        const decodedToken= decode(token)
        if(decodedToken.exp * 1000 < new Date(). getTime()){
          handleLogout
        }
      }
      dispatch(setCurrentuser(JSON.parse(localStorage.getItem('Profile'))))
    }, [dispatch])

  return (
    <nav className='main-nav'>
    <div className='Navbar'>
      <Link to='/' className='nav-item nav-logo'>
        <img src={logo} alt='logo' width='150'/>
      </Link>  
      <Link to='/' className='nav-item nav-btn'>About</Link> 
      <Link to='/' className='nav-item nav-btn'>Production</Link> 
      <Link to='/' className='nav-item nav-btn'>For Items</Link> 
      <form>
        <input type='text' placeholder='Search....'/>
        < img src={search} alt='search' height='15' width='20' className='search-icon'/>
        
      </form>

        {User===null ?
          <Link to='/Auth' className='nav-item nav-links'>Log in</Link>  :
          <>
          <Avatar backgroundColor='#009dff' px="10px" py="7px" borderRadius="50%" color='white'> <Link to='/User' style={{color:'white' ,textDecoration:"none"}}> {User.result.name.charAt(0).toUpperCase()}</Link> </Avatar>
          <button className='nav-item nav-links' onClick={handleLogout}>Log out</button>
          </>
        }

    </div>
    </nav>
  )
}

export default navbar ;
